gulp.task("build", function () {
    var result = gulp
        .src("./src/*.ts")
        .pipe(gts(gts.createProject("./tsconfig.json")));
    return result
        .js
        .pipe(gulp.dest("./lib/"));
});
gulp.task("todo", function () {
    return gulp
        .src("./src/*.ts")
        .pipe(todo({
        fileName: "TODO.md"
    }))
        .pipe(gulp.dest('./'));
});
gulp.task("watch", function () {
    gulp.watch("./src/*.ts", ["build"]);
    gulp.watch("./src/*.ts", ["todo"]);
});
